//jQuery
/* jQuery von WordPress deaktivieren und Ã¼ber Google laden */
function my_scripts_method() {
    wp_deregister_script( 'jquery' );
    wp_register_script( 'jquery', 'http://ajax.googleapis.com/ajax/libs/jquery/1.8.2/jquery.min.js'); // hier kann auch eine andere Quelle angegeben werden, z.B. jQuery oder Microsoft. Siehe: http://jquery.com/download/ 
    wp_enqueue_script( 'jquery' );
}  
add_action('wp_enqueue_scripts', 'my_scripts_method');


